function setCookie(n,t,i){const r=new Date;r.setTime(r.getTime()+i*864e5);const u="expires="+r.toUTCString();document.cookie=`${encodeURIComponent(n)}=${encodeURIComponent(t)};${u};path=/`}function getCookie(n){let t=n+"=",i=document.cookie.split(";");for(let n=0;n<i.length;n++){let r=i[n];while(r.charAt(0)===" ")r=r.substring(1);if(r.indexOf(t)===0)return r.substring(t.length,r.length)}return""}(async function(){let n="user_ip";const t=getCookie(n);if(t===""){const t=await fetch("https://api.my-ip.io/v2/ip.json").then(n=>n.json());setCookie(n,JSON.stringify(t),1)}})();